import {onBeforeRouteUpdate } from 'vue-router';
export default {
  props: ['id','imgSrc', 'imgNameSrc', 'cv', 'info', 'property'],
  
  setup(){
    const data=reactive({
      characterid:""
    })
    onBeforeRouteUpdate(to=>{
