<script lang="ts">
import axios from 'axios'
import {useMainStore} from '@/store'
import { storeToRefs } from 'pinia'
const sotre=useMainStore()
import { reactive } from 'vue'
export default {
  setup() {
    const httpIp="http://127.0.0.1:5000/"
    const {isShowLogin}=storeToRefs(sotre)
    const data = reactive({
      isShowLogin,
      loginWay:true,
      username: '',
      password: ''
    })
    const sendAjax=()=> {
      axios.post(`${httpIp}login`, {
        username: data.username,
        password: data.password
      }).then(
        response => {
          const { isLogin } = response.data
          console.log(isLogin)
          if (isLogin) {
            //this.CHANGE_IS_SHOW_LOGIN(false)// 关闭登录界面
            //this.CHANGE_LOGIN_STATUS(true)// 改变登录状态
            //const hiddenName = this.username.replace(this.username.substring(3, 7), '****')// 加密用户名
            //this.CHANGE_USER_NAME(hiddenName)// 改变用户名
            //localStorage.setItem('username', hiddenName)// 存入用户名
            //localStorage.setItem('loginStatus', true)// 存入登录状态
          } else {
            alert('用户名或密码错误')
          }
        }
      )
    }
    return {
      data,
      sendAjax
    }
  },
  methods: {
    
  }
}
</script>
